import React from 'react';
import { Button, Card, Header } from 'semantic-ui-react';
import './styles.css'

const data = [
    {
        nota: "10",
        fecha: "Mon 03-04-2020 9:00",
        alumno: {
            legajo: "68694",
            nombre: "Juan",
            apellido: "Perez",
            añoCursado: "5",
            carrera: "Ingeniería en Sistemas de Información",
            catedra: "Arquitectura de Software",
            profesor: "Pablo Frías"
        },
        instancia: {
            tipo: "Parcial",
            numero: "2"
        },
        observaciones: "Sin observaciones"
    },
    {
        nota: "10",
        fecha: "Mon 03-04-2020 9:00",
        alumno: {
            legajo: "68694",
            nombre: "Juan",
            apellido: "Perez",
            añoCursado: "2",
            carrera: "Ingeniería en Sistemas de Información",
            catedra: "Sistemas Operativos",
            profesor: "Cecilia Sanchez"
        },
        instancia: {
            tipo: "Parcial",
            numero: "1"
        },
        observaciones: "Sin observaciones"
    },
    {
        nota: "8",
        fecha: "Mon 03-04-2020 9:00",
        alumno: {
            legajo: "68694",
            nombre: "Juan",
            apellido: "Perez",
            añoCursado: "2",
            carrera: "Ingeniería en Sistemas de Información",
            catedra: "Comunicaciones",
            profesor: "xxx"
        },
        instancia: {
            tipo: "Parcial",
            numero: "1"
        },
        observaciones: "Sin observaciones"
    }
]
      

export default function Materias(props) {
    return(
        <div className='utn'>            
            <Card.Group itemsPerRow={1}>
                {data.map((data, index)=>(
                    <Card key={index}>
                        <Card.Content>
                            <Card.Header>{data.alumno.catedra}</Card.Header>
                            <Card.Meta>Año: {data.alumno.añoCursado}</Card.Meta>
                            <Card.Description>Descripcion de algo</Card.Description>
                        </Card.Content>
                        <Card.Content extra>
                            <Button basic color='red'>
                                Aula virtual
                            </Button>
                        </Card.Content>
                    </Card>
                ))}
            </Card.Group>
                

        </div>

    )
}
