import React from 'react';
import { Button, Card, Modal, Image, Header, Icon } from 'semantic-ui-react';
import _ from 'lodash'
import './styles.css'

const materias = [
    {
        catedra: "Arquitectura de Software",
        profesor: "Frías, Pablo",
        añoCursado: "5",
        instancia: [
            {
                tipo: "Pacial",
                numero: "1",
                fecha: "Mon 06-04-2020 9:00",
                nota: "8",

            },
            {
                tipo: "Pacial",
                numero: "2",
                fecha: "Fri 12-06-2020 9:00",
                nota: "7",

            },
            {
                tipo: "TPI",
                numero: "",
                fecha: "Mon 15-06-2020 9:00",
                nota: "10",

            },
        ],
    },
    {
        catedra: "Sistemas de Gestión",
        profesor: "Gualpa, Mariano Martín",
        añoCursado: "5",
        instancia: [
            {
                tipo: "Pacial",
                numero: "1",
                fecha: "Mon 06-04-2020 9:00",
                nota: "7",

            },
            {
                tipo: "Pacial",
                numero: "2",
                fecha: "Fri 12-06-2020 9:00",
                nota: "9",

            },
        ],
    },
    {
        catedra: "Inteligencia Artificial",
        profesor: "Destefanis, Eduardo Atilio",
        añoCursado: "5",
        instancia: [
            {
                tipo: "Pacial",
                numero: "1",
                fecha: "Mon 06-04-2020 12:00",
                nota: "8",

            },
            {
                tipo: "Pacial",
                numero: "2",
                fecha: "Wed 10-06-2020 16:00",
                nota: "6",

            },
        ],
    },
    {
        catedra: "Inteligencia de Negocios",
        profesor: "Magliano, Hernan Dario",
        añoCursado: "5",
        instancia: [
            {
                tipo: "Pacial",
                numero: "1",
                fecha: "Mon 06-04-2020 12:00",
                nota: "8",

            },
            {
                tipo: "TPI",
                numero: "",
                fecha: "Wed 10-06-2020 16:00",
                nota: "10",

            },
        ],
    },
    
    
]
      
function modal(materia){
    return <Modal closeIcon trigger={<Button basic color='red' size='small'>Aula Virtual</Button>}>
        
        <Modal.Header>{materia.catedra}</Modal.Header>
        <Modal.Content image scrolling>

            <Modal.Description>
                <Header>{materia.profesor}</Header>
                <p>
                This is an example of expanded content that will cause the modal's
                dimmer to scroll
                </p>

                {_.times(8, (i) => (
                <Image
                    key={i}
                    src='/images/wireframe/paragraph.png'
                    style={{ paddingBottom: 5 }}
                />
                ))}
            </Modal.Description>
        </Modal.Content>
        <Modal.Actions>
            <Button primary>
                OK <Icon name='chevron right' />
            </Button>
        </Modal.Actions>
    </Modal>
}

export default function Materias(props) {
    return(
        <div className='utn'>            
            <Card.Group itemsPerRow={1}>
                {materias.map((materia, index)=>(
                    <Card key={index}>
                        <Card.Content>
                            <Card.Header>{materia.catedra}</Card.Header>
                            <Card.Meta>Año: {materia.añoCursado}</Card.Meta>
                            <Card.Description>{materia.profesor}</Card.Description>
                        </Card.Content>

                        <Card.Content extra>
                            {modal(materia)}
                        </Card.Content>
                            
                    </Card>
                ))}
            </Card.Group>    

        </div>

    )
}
